/* Painel Principal - Cápsula Ajustada */
.dashboard-header {
  background-color: #021f1e;
  border-radius: 125px; /* Cápsula perfeita */
  box-shadow: 0 8px 25px rgba(0, 0, 0, 0.2);
  border: -900001px solid rgba(4, 204, 194, 0.3);
  backdrop-filter: blur(10px);
  max-width: 80%; /* Impede que o painel estique demais */
  min-width: 600px;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  margin: center auto; /* Centraliza */
}

/* Container do Título */
.title-container {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 12px;
  flex-wrap: wrap;
}

/* Ícone */
.icon {
  width: 40px;
  height: 40px;
  filter: drop-shadow(0 0 6px rgba(49, 251, 241, 0.6));
}

/* Título Principal */
.title {
  font-size: 28px;
  font-weight: bold;
  color: #ffffff;
  text-shadow: 0 2px 6px rgba(0, 0, 0, 0.4);
  margin: 0;
}

/* Separador */
.separator {
  font-size: 28px;
  font-weight: bold;
  color: rgba(255, 255, 255, 0.3);
}

/* Destaque */
.highlight {
  font-size: 28px;
  font-weight: bold;
  color: #31FBF1;
  text-shadow: 0 0 12px rgba(49, 251, 241, 0.7);
}

/* Container de Status */
.status-indicator {
  display: flex;
  align-items: center;
  gap: 12px;
  margin-top: 18px;
  flex-wrap: wrap;
  justify-content: center;
}

/* Itens de Status */
.status-item {
  display: flex;
  align-items: center;
  gap: 6px;
  font-size: 15px;
  color: #ffffff;
  padding: 8px 18px;
  border-radius: 9999px;
  background: rgba(255, 255, 255, 0.15);
  transition: all 0.3s ease;
}

.status-item:hover {
  background: rgba(255, 255, 255, 0.25);
  transform: scale(1.05);
}

/* Pontos de Status */
.status-dot {
  width: 12px;
  height: 12px;
  border-radius: 50%;
}

/* Cores dos Status */
.status-normal {
  background-color: #02EDE1;
  box-shadow: 0 0 8px #02EDE1;
}

.status-warning {
  background-color: #EDE102;
  box-shadow: 0 0 8px #EDE102;
}

.status-critical {
  background-color: #ED020E;
  box-shadow: 0 0 8px #ED020E;
}

